import React from 'react';
import './dashboard.css';
import Card from 'react-bootstrap/Card';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Doughnut } from 'react-chartjs-2';

class Dashboard extends React.Component {
  constructor(){
    super();
    this.state = {
      best_courses : []
    }
  }

 calculatebestcourses = (id,admis,total)=>{
   if (total!= 0){
    this.state.best_courses.push({"id":id, "winrate": (admis/total).toFixed(2)})
    

   }
    
}
  componentDidMount() {
   fetch('http://127.0.0.1:8000/best_courses/').
   then(res => res.json()).
   then(
     result =>{
      for(var i=0; i< result.length;i++){
       this.calculatebestcourses(result[i]["cours_id"],result[i].admis_number,result[i].student_number)
      }
     }

   )

  }
  render(){
    return (
      <div className="Dashboard">
        <div className="container">
          <div className="row">

            <article className="col-md-10">

              <section>
                <header>header1</header>
                  <div id="general-info">
                  <Card id="best-courses"  style={{ width: '18rem' }}>
                    <Card.Body>{this.state.best_courses.map( (result => (
                          <p>result</p>
                    )))}</Card.Body>
                  </Card>
                  <Card id="popular-bacfield"  style={{ width: '18rem' }}>
                    <Card.Body>popular field</Card.Body>
                  </Card>
                  <Card id="best-bacfield"  style={{ width: '18rem' }}>
                    <Card.Body>best field</Card.Body>
                  </Card>
                  <Card id="best-gender"  style={{ width: '18rem' }}>
                    <Card.Body>best gender</Card.Body>
                  </Card>
                  </div>              
              </section>

              <section>
                <header>header2</header>
                <div id="year-analysis"></div>
              </section>  

            </article>

            <aside className="col-md-2">

              <div>

              </div>
            </aside>

          </div>    
        </div>
      </div>
      
      
    );
  }
 
}

export default  Dashboard;
