import React from 'react';
import './profile.css';

import 'bootstrap/dist/css/bootstrap.min.css';

class Profile extends React.Component {
  constructor(){
    super();
    this.state = {

      default_student: 14011599308
    }
  }
  getStudents = (result)=>{
     var temp = []
     for(var i=0;i< result.length; i++){
       temp.push(result[i]['student'])
       
     }
     console.log(temp[0])

     this.setState({
      students : [...temp],
     })
  }

  connectToPerformance = ()=>{
    
  fetch('http://127.0.0.1:8000/performance/').
    then(res => res.json()).
    then(
      result =>{
        this.getStudents(result)    
      }
    )
  }

  connectToCourses = ()=>{
    
    fetch('http://127.0.0.1:8000/courses/').
      then(res => res.json()).
      then(
        result =>{
          this.getSemesterCourses(result)    
        }
      )
    }

  componentDidMount() {
    this.connectToPerformance()
  }
  render(){
    return (
      
      <div className="Profile">
        { 
          /* the header we can choose a student */
        }

        <header >
        <div class="input-group mb-3">
  <div class="input-group-prepend">
    <button class="btn btn-outline-secondary" type="button">Button</button>
  </div>
  <input  value={this.state.default_student}type="text" class="form-control" placeholder="" aria-label="" aria-describedby="basic-addon1"/>
</div>
       
      
      
        </header>

        { 
          /* here you can see a radar about student performance in courses */
        }

        <article>
         <section></section>
         <section></section>
        </article>

        { 
          /* more details info */
        }
        
        <article>
         <section></section>
         <section></section>
        </article>
      </div>
    );
  }
 
}

export default  Profile;
